      //     </nav>
          
      //       <div className="PageSwitcher">
      //         <NavLink
      //           to="/sign-in"
      //           activeClassName="PageSwitcher__Item--Active"
      //           className="PageSwitcher__Item"
      //         >
      //          <Popup modal trigger = { <button
      //             id="button1"
      //             onClick={() => this._onButtonClick("button1")}
      //           >
      //             Sign In
      //           </button>}></Popup>
      //         </NavLink>
      //         <NavLink
      //           exact
      //           to="/"
      //           activeClassName="PageSwitcher__Item--Active"
      //           className="PageSwitcher__Item"
      //         >
      //          <Popup modal trigger = {<button
      //             id="button2"
      //             onClick={() => this._onButtonClick("button2")}
      //           >
      //             Sign Up
      //           </button>}></Popup>
      //         </NavLink>
      //       </div>

      //       <div className="button">
      //         {this.state && this.state.button1 && "button1 clicked" ? (
      //           <SignInForm />
      //         ) : null}
      //         {this.state && this.state.button2 && "button2 clicked" ? (
      //           <SignUpForm />
      //         ) : null}
      //       </div>
      // </header>
      function Example() {
        const [show, setShow] = useState(false);
      
        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
      
        return (
          <>
            <Button variant="primary" onClick={handleShow}>
              Launch demo modal
            </Button>
      
            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={handleClose}>
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
          </>
        );
      }
    );
  }
}

class FooterComponent extends Component {
  render() {
    return (
      <footer className="footer">
        <span className="text-muted">All Rights Reserved 2018 @CitySource</span>
      </footer>
    );
  }
}
