import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Form, Button, FormGroup, FormControl, ControlLabel, Col } from "react-bootstrap";

class SignUpForm extends Component {
  constructor() {
    super();

    this.state = {
      email: "",
      password: "",
      name: "",
      hasAgreed: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    let target = e.target;
    let value = target.type === "checkbox" ? target.checked : target.value;
    let name = target.name;

    this.setState({
      [name]: value
    });
  }

  handleSubmit(e) {
    e.preventDefault();

    console.log("The form was submitted with the following data:");
    console.log(this.state);
  }

  handleClose = () => {
    this.setState({ isOpen: false });
  };

  render() {
    return (
      <div className="FormCenter">
        <form
          onSubmit={this.handleSubmit}
          onSubmit={this.handleClose}
          className="FormFields"
        >
          <div className="FormField">
            <label className="FormField__Label" htmlFor="fname">
              First Name
            </label>
            <input
              type="text"
              id="fname"
              className="FormField__Input"
              placeholder="Enter your first name"
              name="fname"
              value={this.state.fname}
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <label className="FormField__Label" htmlFor="lname">
              Last Name
            </label>
            <input
              type="text"
              id="lname"
              className="FormField__Input"
              placeholder="Enter your last name"
              name="lname"
              value={this.state.lname}
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <label className="FormField__Label" htmlFor="email">
              E-Mail Address
            </label>
            <input
              type="email"
              id="email"
              className="FormField__Input"
              placeholder="Enter your email"
              name="email"
              value={this.state.email}
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <label className="FormField__Label" htmlFor="number">
              Phone Number
            </label>
            <input
              type="number"
              id="number"
              className="FormField__Input"
              placeholder="Enter your contact number"
              name="number"
              value={this.state.number}
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <label className="FormField__Label" htmlFor="password">
              Password
            </label>
            <input
              type="password"
              id="password"
              className="FormField__Input"
              placeholder="Enter your password"
              name="password"
              value={this.state.password}
              onChange={this.handleChange}
            />
          </div>
          <div className="FormField">
            <label className="FormField__CheckboxLabel">
              <input
                className="FormField__Checkbox"
                type="checkbox"
                name="hasAgreed"
                value={this.state.hasAgreed}
                onChange={this.handleChange}
              />{" "}
              I agree all statements in{" "}
              <a href="" className="FormField__TermsLink">
                terms of service
              </a>
            </label>
          </div>

          <div className="FormField">
            <button className="FormField__Button mr-20">Sign Up</button>{" "}
            <Link to="/sign-in" className="FormField__Link">
              I'm already member
            </Link>
          </div>
        </form>
      </div>
    );
  }
}
export default SignUpForm;
