import React, { PureComponent } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import Rater from "react-rater";
import "react-rater/lib/react-rater.css";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Fade } from "react-slideshow-image";
import Map from "./Map";
import ls from "local-storage";
import { Form } from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import {ToastsContainer, ToastsStore} from 'react-toasts';


const fadeProperties = {
    duration: 5000,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    onChange: (oldIndex, newIndex) => {
      console.log(`fade transition from ${oldIndex} to ${newIndex}`);
    }
  };

class Details_Restaurant extends PureComponent{
  constructor (props){
    super (props);
    this.state={
      place_details:[],
      allComments:[],
      comment:"",
      newPostAdded:false,
      establishments: [],
        locations: [],
        lat: " ",
        long: " ",
        showModal1: false,
        updatedComment:"",
        updatedPostId:"",
        isliked:false,
        upvotes:0,
        downvotes:0,
    };

    this.getCoordinates = this.getCoordinates.bind(this);
    this.getPlaceDetails = this.getPlaceDetails.bind(this);
    this.addComment = this.addComment.bind(this);
    this.onChange = this.onChange.bind(this);
    this.fetchComments = this.fetchComments.bind(this);
    this.changeState = this.changeState.bind(this);
    this.updateComment = this.updateComment.bind(this);
    this.deleteComment = this.deleteComment.bind(this);

  }
  componentWillMount() {
  this.fetchComments();
}
componentDidMount() {
  this.getPlaceDetails();
}

fetchComments() {
  var postData = {
    "postsubjectname": ls.get("selectedIndex")["name"]
  }

  return axios({
      method: "post",
      url: "http://localhost:8080/getrestaurantposts",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {
      //change state of all comments and re render

      this.setState({
        allComments: response.data
      });
    })
    .catch(err => {
      console.log(err);
    });
}

onChange(e) {
  this.setState({
    [e.target.name]: e.target.value
  });
}

getCoordinates() {
  return axios({
      method: "post",
      url: "http://localhost:8080/getPosition",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: ls.get("city")
    })
    .then(response => {
      this.setState({
        lat: response.data.lat
      });
      this.setState({
        long: response.data.long
      });
    })
    .catch(err => {
      console.log(err);
    });
}



componentDidMount() {
  this.getCoordinates();
}

getPlaceDetails() {
  return axios({
      method: "post",
      url: "http://localhost:8080/getPlaceDetails",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: ls.get("selectedIndex")["place_id"]
    })
    .then(response => {
      this.setState({
        place_details: response.data.results
      });
      //this.setState({ locations: response.data.results });
    })
    .catch(err => {
      console.log(err);
    });
}

addComment() {
  var data=this.refs.comment.value;
  var postData = {
    "username": ls.get('currentUser'),
    "title": "",
    "ratings": 0,
    "datetime": "",
    "category": "Restaurant",
    "postsubjectname": ls.get("selectedIndex")["name"],
    "postContent": data,
    "upvotes":0,
    "downvotes":0
  }

  return axios({
      method: "post",
      url: "http://localhost:8080/saverestaurantpost",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {
      //change state to re render component
      this.setState({
        newPostAdded: true
      })
      this.refs.comment.value="";
      this.fetchComments();
      //this.sendNotifications();
      ToastsStore.success("New Post Added");

    })
    .catch(err => {
      console.log("error " + err);
    });

}

handleClick(index) {
  console.log(index)
  if (ls.get("currentUser") == index["username"])
    this.setState({
      showModal1: true,
      updatedComment: index["postContent"],
      updatedPostId: index["postId"],
      upvotes:index["upvotes"],
      downvotes:index["downvotes"]
    })
  }

changeState() {
  this.setState({
    showModal1: !this.state.showModal1
  })
}

updateComment(index) {
  //axios call to update this comment
  var postData = {
    "username": ls.get("currentUser"),
    "title": "",
    "ratings": 0,
    "datetime": "",
    "category": "Restaurant",
    "postsubjectname": index["postsubjectname"],
    "postContent": this.state.updatedComment,
    "postId": this.state.updatedPostId,
    "upvotes":this.state.upvotes,
    "downvotes":this.state.downvotes,
  }
  return axios({
      method: "post",
      url: "http://localhost:8080/updateRestaurantPost",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {

      this.changeState();
      this.fetchComments();
      ToastsStore.success("Post Updated");
    })
    .catch(err => {
      console.log("error while updating" + err);
    });
}

deleteComment(index) {
  var postData = {
    "username": ls.get("currentUser"),
    "title": "",
    "ratings": 0,
    "datetime": "",
    "category": "Restaurant",
    "postsubjectname": index["postsubjectname"],
    "postContent": this.state.updatedComment,
    "postId": this.state.updatedPostId,
    "upvotes":this.state.upvotes,
    "downvotes":this.state.downvotes
  }
  return axios({
      method: "post",
      url: "http://localhost:8080/deleteRestaurantPost",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {
      this.changeState();
      this.fetchComments();
      ToastsStore.success("Post Deleted");

      //change state to re render component
    })
    .catch(err => {
      console.log("error while deleting" + err);
    });
}


upvote(index) {
  var count = index['upvotes'] + 1;
  var postData = {
    "username": ls.get("currentUser"),
    "title": "",
    "ratings": 0,
    "datetime": "",
    "category": "Restaurant",
    "postsubjectname": index["postsubjectname"],
    "postContent": index['postContent'],
    "postId": index['postId'],
    "upvotes":count,
    "downvotes":index['downvotes']
  }
  return axios({
      method: "post",
      url: "http://localhost:8080/updateRestaurantPost",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {
      console.log("update success" + response.data);
      this.fetchComments();
    })
    .catch(err => {
      console.log("error while updating" + err);
    });
}

downvote(index) {

  var count = index['downvotes'] + 1;

  this.setState({
    updatedPostId: index["postId"]
  })

  var postData = {
    "username": ls.get("currentUser"),
    "title": "",
    "ratings": 0,
    "datetime": "",
    "category": "Restaurant",
    "postsubjectname": index["postsubjectname"],
    "postContent":  index['postContent'],
    "postId":  index['postId'],
    "downvotes":count,
    "upvotes":index['upvotes']
  }
  return axios({
      method: "post",
      url: "http://localhost:8080/updateRestaurantPost",
      headers: {
        "Access-Control-Allow-Origin": "*"
      },
      data: postData
    })
    .then(response => {
      console.log("update success" + response.data);
      this.fetchComments();
    })
    .catch(err => {
      console.log("error while updating" + err);
    });
}

