import React, { Component } from "react";
import { HashRouter as Router, Route, Link, NavLink } from "react-router-dom";
import SignUpForm from "./pages/SignUpForm";
import SignInForm from "./pages/SignInForm";
import { Form, Button, FormGroup, FormControl, ControlLabel, Col, Nav, Navbar } from "react-bootstrap";
import mobiscroll from '@mobiscroll/react';
import '@mobiscroll/react/dist/css/mobiscroll.min.css';


mobiscroll.settings = {
  theme: 'ios'
};

const signUpBtn = [{
  text: 'Sign Up',
  handler: 'set'
}];

const logInBtn = [{
  text: 'Log In',
  handler: 'set'
}];

export default class HomeComponent extends Component {
  render() {
    return (
      <div className="HomeComponent">
        <Router>
            <HeaderComponent />
            {/*<Switch>
                            <Route path="/" exact component={FooterComponent}/>
                            <Route path="/SignUpForm" component={SignUpComponent}/>
                            <Route path="/welcome/:name" component={WelcomeComponent}/>
                            <Route path="/logout" component={LogoutComponent}/>
                            <Route path="/SignInForm" component={SignInForm.SignInComponent}/>
                            <Route component={ErrorComponent}/>
                        </Switch>*/}
            <FooterComponent />
        </Router>
      </div>
    );
  }
}

class HeaderComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showComponent: false
    };
    this._onButtonClick = this._onButtonClick.bind(this);
  }

  _onButtonClick = id => {
    this.setState({ [id]: true });
  };
  render() {
    return (
      <header>
          <div>
                <mobiscroll.Form>
                    <mobiscroll.FormGroup>
                        <div className="mbsc-btn-group-block">
                            <mobiscroll.Button onClick={this.showRegister}>Show register form</mobiscroll.Button>
                            <mobiscroll.Button onClick={this.showLogin}>Show login form</mobiscroll.Button>
                        </div>
                    </mobiscroll.FormGroup>
                </mobiscroll.Form>
                <mobiscroll.Popup
                    ref="register"
                    display="center"
                    buttons={signUpBtn}
                >
                    <mobiscroll.Form>
                        <mobiscroll.FormGroup inset>
                            <mobiscroll.Input type="text" name="name" placeholder="Name" />
                            <mobiscroll.Input type="text" name="email" placeholder="Email" />
                            <mobiscroll.Input name="password" type="text" placeholder="Password" passwordToggle="true" iconShow="eye" iconHide="eye-blocked" />
                        </mobiscroll.FormGroup>
                    </mobiscroll.Form>
                </mobiscroll.Popup>
                <mobiscroll.Popup
                    ref="login"
                    display="center"
                    buttons={logInBtn}
                >
                    <mobiscroll.Form>
                        <mobiscroll.FormGroup inset>
                            <mobiscroll.Input type="text" name="email" placeholder="Email" />
                            <mobiscroll.Input name="password" type="text" placeholder="Password" passwordToggle="true" iconShow="eye" iconHide="eye-blocked" />
                        </mobiscroll.FormGroup>
                        <div className="mbsc-align-center">or log in with one of your existing accounts</div>
                        <mobiscroll.FormGroup inset>
                            <mobiscroll.Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#3c5c9a', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-ion-social-facebook mbsc-pull-left"></span>
                                Log in with Facebook
                            </mobiscroll.Button>
                            <mobiscroll.Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#28aae1', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-fa-twitter mbsc-pull-left"></span>
                                Log in with Twitter
                            </mobiscroll.Button>
                            <mobiscroll.Button onClick={this.hideLogin} className="md-social-btn mbsc-btn-block" style={{background:'#d34230', color:'#fff'}}>
                                <span className="mbsc-ic mbsc-ic-fa-google mbsc-pull-left"></span>
                                Log in with Google
                            </mobiscroll.Button>
                        </mobiscroll.FormGroup>
                    </mobiscroll.Form>
                </mobiscroll.Popup>
            </div>
       {/* <nav className="navbar navbar-expand-md navbar-dark bg-dark">
          <div>
            <a href="" className="navbar-brand">
              <h1>CitySource</h1>
            </a>
            </div>
          </nav>
          
            <div className="PageSwitcher">
              <NavLink
                to="/sign-in"
                activeClassName="PageSwitcher__Item--Active"
                className="PageSwitcher__Item"
              >
                <button
                  id="button1"
                  onClick={() => this._onButtonClick("button1")}
                >
                  Sign In
                </button>
              </NavLink>
              <NavLink
                exact
                to="/"
                activeClassName="PageSwitcher__Item--Active"
                className="PageSwitcher__Item"
              >
                <button
                  id="button2"
                  onClick={() => this._onButtonClick("button2")}
                >
                  Sign Up
                </button>
              </NavLink>
            </div>

            <div className="button">
              {this.state && this.state.button1 && "button1 clicked" ? (
                <SignInForm />
              ) : null}
              {this.state && this.state.button2 && "button2 clicked" ? (
                <SignUpForm />
              ) : null}
            </div> */}
      </header>
    );
  }
}

class FooterComponent extends Component {
  render() {
    return (
      <footer className="footer">
        <span className="text-muted">All Rights Reserved 2018 @CitySource</span>
      </footer>
    );
  }
}
